INSERT INTO `category_has_merchant` (`category_id`,`merchant_id`,`price`,`is_active`,`id`,`time_in_minutes`,`additional_time`,`service_time_slot`,`title`,`group_people`,`is_group`,`staff_id`,`color`,`description`) VALUES
('74','48','0.00','1','162','90','','','REVIDERM PCA Pee­lings','0','0','','#1976d2','<p>Das PCA Peeling von REVIDERM sorgt für einen Energie-Push in Ihrer Haut. Das einzigartige Peeling besteht aus einem Komplex an Fruchtsäuren, der die Haut von Verhornungen befreit und aufhellt. Es besteht aus Alpha Hydroxy und Beta Hydroxy Säuren. Diese sorgen für eine ebenmässige uns sehr gut verträgliche Hautabtragung. Das Peeling ist ideal zur Behandlung von Pigmentstörungen,  Akne, Narben und zur Hautverbesserung bei grobporiger, sonnengeschädigter, müder und schlaffer Haut.</p><p><strong><br>Unsere Programme:</strong> PCA Peel Intense, PCA Peel Power Lift, PCA Peel Hand Lightening</p>');
